document.addEventListener("DOMContentLoaded",(()=>{const e=document.querySelector("#start_video"),o=document.querySelector("#stop_video");e.addEventListener("click",(()=>{chrome.tabs.query({active:!0,currentWindow:!0},(function(e){chrome.tabs.sendMessage(e[0].id,{action:"request_recording"},(function(e){chrome.runtime.lastError?console.log(chrome.runtime.lastError,"error line 14"):console.log(e)}))}))})),o.addEventListener("click",(()=>{chrome.tabs.query({active:!0,currentWindow:!0},(function(e){chrome.tabs.sendMessage(e[0].id,{action:"stopvideo"},(function(e){chrome.runtime.lastError?console.log(chrome.runtime.lastError,"error line 27"):console.log(e)}))}))}))}));